pragma solidity 0.5.16;
import "../centre-tokens/contracts/FiatTokenV1.sol";
import "../compound-protocol/contracts/CErc20.sol";
import "./DonationAccountFactory.sol";

contract DeFiDonation {
  FiatTokenV1 public usdc;
  CErc20 public cusdc;
  mapping(address => address) public donation_accounts;
  DonationAccountFactory public daf;
  address[] public projects;

  constructor(address _usdc_address, address _cusdc_address, address _daf_address) public {
	  usdc = FiatTokenV1(_usdc_address);
	  cusdc = CErc20(_cusdc_address);
	  daf = DonationAccountFactory(_daf_address);
  }

  function createDonationAccount() public {
	  address donationAccount = daf.createInstance(address(usdc), address(cusdc));
	  donation_accounts[msg.sender] = donationAccount;
  }

  function isAccountExists() public view returns (bool){
	  return donation_accounts[msg.sender] != address(0);
  }

  function getDonationAccount() public view returns (address) {
	  require(donation_accounts[msg.sender] != address(0));
	  return donation_accounts[msg.sender];
  }
}
